INSERT INTO `zooinfo`.`classe` (`codigoClasse`, `descricaoClasse`, `nomeClasse`) VALUES ('1', 'Taxonómica do filo Mollusca que agrupa os animais conhecidos por caracóis, lesmas, lapas e búzios', 'Gastropoda');
INSERT INTO `zooinfo`.`classe` (`codigoClasse`, `descricaoClasse`, `nomeClasse`) VALUES ('2', 'classe de moluscos marinhos a que pertencem os polvos, as lulas, os náutilos e os chocos.', 'Cephalopoda');
INSERT INTO `zooinfo`.`classe` (`codigoClasse`, `descricaoClasse`, `nomeClasse`) VALUES ('3', 'classe do filo Mollusca que inclui os animais aquáticos popularmente designados por bivalves.', 'Bivalvia');
INSERT INTO `zooinfo`.`classe` (`codigoClasse`, `descricaoClasse`, `nomeClasse`) VALUES ('4', 'Artrópodes que inclui, entre outros, aranhas, carrapatos, ácaros, opiliões e escorpiões, compreendendo mais de 60.000 espécies distintas.', 'Arachnida');
INSERT INTO `zooinfo`.`classe` (`codigoClasse`, `descricaoClasse`, `nomeClasse`) VALUES ('5', 'São animais invertebrados artropódes. Entre eles estão alguns dos animais mais comuns que conhecemos, como siris, caranguejos, tatuzinhos-de-jardim, lagostas, cracas e camarões.', 'Crustacea');
INSERT INTO `zooinfo`.`classe` (`codigoClasse`, `descricaoClasse`, `nomeClasse`) VALUES ('6', 'classe do subfilo Myriapoda, vulgarmente conhecidos como piolhos-de-cobra.', 'Diplopoda');
INSERT INTO `zooinfo`.`classe` (`codigoClasse`, `descricaoClasse`, `nomeClasse`) VALUES ('7', 'Uma das classes pertencente ao subfilo Myriapoda. Nesta classe está a ordem Scolopendromorpha, que por sua vez se subdivide na família Scolopendridae, mais conhecida.', 'Chilopoda');
INSERT INTO `zooinfo`.`classe` (`codigoClasse`, `descricaoClasse`, `nomeClasse`) VALUES ('8', 'Os insetos são animais invertebrados artrópodes, pertencentes ao Filo Arthropoda e classe Insecta.', 'Insecta');
INSERT INTO `zooinfo`.`classe` (`codigoClasse`, `descricaoClasse`, `nomeClasse`) VALUES ('9', 'A classe Pisces, de Lineu é considerada tipológica, mas não filogenética. Os primeiros organismos que podem ser classificados como peixes eram cordados de corpo mole que apareceram pela primeira vez durante o período Cambriano.', 'Pisces');
INSERT INTO `zooinfo`.`classe` (`codigoClasse`, `descricaoClasse`, `nomeClasse`) VALUES ('10', 'Anfíbios são animais vertebrados que vivem entre o meio aquático e o ambiente terrestre. Eles mantêm uma forte vinculação com a água e dela não se afastam, pois precisam manter a pele úmida. A fecundação geralmente é externa e ocorre na água.', 'Amphilbia');
INSERT INTO `zooinfo`.`classe` (`codigoClasse`, `descricaoClasse`, `nomeClasse`) VALUES ('11', 'Animais vertebrados tetrápodes e ectotérmicos, ou seja, não possuem temperatura corporal constante. São todos amniotas, característica que os permitiu ficarem independentes da água para reprodução, ao contrário dos anfíbios.', 'Reptilia');
INSERT INTO `zooinfo`.`classe` (`codigoClasse`, `descricaoClasse`, `nomeClasse`) VALUES ('12', 'Seres vivos vertebrados endotérmicos caracterizada pela presença de penas, um bico sem dentes, oviparidade de casca rígida, elevado metabolismo, um coração com quatro câmaras e um esqueleto pneumático resistente e leve. ', 'Aves');
INSERT INTO `zooinfo`.`classe` (`codigoClasse`, `descricaoClasse`, `nomeClasse`) VALUES ('13', 'Os mamíferos são os maiores animais tanto terrestres quanto aquáticos, mas não são os mais diversos dos tetrápodes, perdendo para as aves e os répteis.', 'Mammalia');
INSERT INTO `zooinfo`.`familia` (`codigo`, `descricaoFamilia`, `nomeFamilia`, `claCodigo`) VALUES ('1', 'Os Columbellidae, os caracóis ou conchas de pombas, são uma família de pequenos a pequenos caracóis do mar, moluscos gastrópodes marinhos da ordem Neogastropoda', 'Columbellidae', '1');
INSERT INTO `zooinfo`.`familia` (`codigo`, `descricaoFamilia`, `nomeFamilia`, `claCodigo`) VALUES ('2', 'Cassidae é uma família de moluscos gastrópodes marinhos predadores, em sua maioria, de equinoides; classificada por Pierre André Latreille, em 1825, e pertencente à subclasse Caenogastropoda, na ordem Littorinimorpha.', 'Cassidae', '1');
INSERT INTO `zooinfo`.`familia` (`codigo`, `descricaoFamilia`, `nomeFamilia`, `claCodigo`) VALUES ('3', 'Fissurellidae, nome comum de lapas de lapa e lapas de fenda, é uma família taxonômica de caracóis marinhos semelhantes a lapas, moluscos gastrópodes marinhos no clado Vetigastropoda.', 'Fissurellidae', '1');
INSERT INTO `zooinfo`.`familia` (`codigo`, `descricaoFamilia`, `nomeFamilia`, `claCodigo`) VALUES ('4', 'Os ágnatos são um grupo parafilético de vertebrados aquáticos que, juntamente com os condríctios e os osteíctios, formam o grupo parafilético conhecido popularmente como peixes. Na classificação tradicional lineana são considerado como uma superclasse.', 'Agnatha', '9');
INSERT INTO `zooinfo`.`familia` (`codigo`, `descricaoFamilia`, `nomeFamilia`, `claCodigo`) VALUES ('5', 'Os condríctios ou peixes cartilagíneos, que incluem os tubarões, as raias e as quimeras, muitas vezes classificadas como Seláceos, são peixes geralmente oceânicos que possuem um esqueleto totalmente formado por cartilagem.', 'Chondrichthyes', '9');
INSERT INTO `zooinfo`.`familia` (`codigo`, `descricaoFamilia`, `nomeFamilia`, `claCodigo`) VALUES ('6', 'Os Actinopterygii ou actinopterígeos são uma classe de peixes com nadadeiras suportadas por \"raios\" ou lepidotríquias, esqueleto interno tipicamente calcificado e aberturas branquiais protegidas por um opérculo ósseo.', 'Actinopterygii', '9');
INSERT INTO `zooinfo`.`familia` (`codigo`, `descricaoFamilia`, `nomeFamilia`, `claCodigo`) VALUES ('7', 'Os Sarcopterygii ou peixes com nadadeiras lobadas, pertencem ao grupo dos Osteichthyes, juntamente com os Actinopterygii.', 'Sarcopterygii', '9');
INSERT INTO `zooinfo`.`familia` (`codigo`, `descricaoFamilia`, `nomeFamilia`, `claCodigo`) VALUES ('8', 'Os sapos são membros da família Bufonidae, pertencente à ordem Anura. Eles são a única família de anuros cujos membros todos são conhecidos como \"sapos\". Os bufonídeos compreendem agora mais de 35 gêneros, Bufo sendo o mais difundido e bem conhecido.', 'Bufonidae', '10');
INSERT INTO `zooinfo`.`familia` (`codigo`, `descricaoFamilia`, `nomeFamilia`, `claCodigo`) VALUES ('9', 'A família Ranidae é constituída por uma grande diversidade de espécies que se distribuem por todo o mundo à excepção do sul da África e da maior parte da Austrália oriental. A rã é um anfíbio anuro da família Ranidae.', 'Ranidae', '10');
INSERT INTO `zooinfo`.`familia` (`codigo`, `descricaoFamilia`, `nomeFamilia`, `claCodigo`) VALUES ('10', 'Hylidae é uma família de anfíbios da ordem Anura, cujas espécies podem ser encontradas em todos os continentes, com exceção da Antártida. São popularmente conhecidas como pererecas, relas ou rainetas.', 'Hylidae', '10');
INSERT INTO `zooinfo`.`familia` (`codigo`, `descricaoFamilia`, `nomeFamilia`, `claCodigo`) VALUES ('11', 'Salamandridae é uma família de anfíbios pertencentes à ordem Caudata. Os animais que fazem parte desta família são vulgarmente chamados de salamandras ou tritões.', 'Salamandridae', '10');
INSERT INTO `zooinfo`.`familia` (`codigo`, `descricaoFamilia`, `nomeFamilia`, `claCodigo`) VALUES ('12', 'Proteidae é uma família de anfíbios pertencentes à ordem Caudata. Esta família esta dividida em 2 géneros: Necturus, com seis espécies no norte da América; Proteus, com apenas uma espécie europeia.', 'Proteidae', '10');
INSERT INTO `zooinfo`.`familia` (`codigo`, `descricaoFamilia`, `nomeFamilia`, `claCodigo`) VALUES ('13', 'Camaleão refere-se a todos os répteis pertencentes à família Chamaeleonidae. É uma das mais conhecidas famílias de lagartos, distribuídos na África, sul da Europa e da Ásia.', 'Chamaeleonidae', '11');
INSERT INTO `zooinfo`.`familia` (`codigo`, `descricaoFamilia`, `nomeFamilia`, `claCodigo`) VALUES ('14', 'A família Viperidae, as famosas víboras, constitui um dos mais notáveis grupos de serpentes do mundo, compreendendo cerca de 362 espécies, e apresentando ampla distribuição geográfica.', 'Viperidae', '11');
INSERT INTO `zooinfo`.`familia` (`codigo`, `descricaoFamilia`, `nomeFamilia`, `claCodigo`) VALUES ('15', 'Testudinidae é a família da ordem Testudines que engloba várias espécies de répteis terrestres, entre as quais os jabutis. Como todos os outros quelónios, os testudinídeos têm o corpo protegido por uma carapaça e plastrão.', 'Testudinidae', '11');
INSERT INTO `zooinfo`.`familia` (`codigo`, `descricaoFamilia`, `nomeFamilia`, `claCodigo`) VALUES ('16', 'Gekkonidae é uma família de répteis escamados, da subordem dos lagartos, que inclui os animais vulgarmente designados no Brasil por lagartixas, taruíras, osgas, gecos, catongas, bribas, bibas, sardaniscas ou sardanitas.', 'Gekkonidae', '11');
INSERT INTO `zooinfo`.`familia` (`codigo`, `descricaoFamilia`, `nomeFamilia`, `claCodigo`) VALUES ('17', 'Jacaré, também chamado aligátor e caimão, são crocodilianos da família Alligatoridae, sendo muito parecidos com os crocodilos, dos quais se distinguem pela cabeça mais curta e larga.', 'Alligatoridae', '11');
INSERT INTO `zooinfo`.`familia` (`codigo`, `descricaoFamilia`, `nomeFamilia`, `claCodigo`) VALUES ('18', 'O papagaio, também conhecido como louro, loiro, ajeru, ajuru, jeru, juru, ou nore, é uma das muitas aves pertencentes à ordem dos Psitaciformes, família Psittacidae, principalmente do gênero Amazona.', 'Psittacidae', '12');
INSERT INTO `zooinfo`.`familia` (`codigo`, `descricaoFamilia`, `nomeFamilia`, `claCodigo`) VALUES ('19', 'Tyrannidae são uma família de aves passeriformes, cuja distribuição estende-se do Alasca à Terra do Fogo, sendo mais concentrada na região neotropical. É considerada atualmente a maior família de aves da Terra.', 'Tyrannidae', '12');
INSERT INTO `zooinfo`.`familia` (`codigo`, `descricaoFamilia`, `nomeFamilia`, `claCodigo`) VALUES ('20', 'Columbidae é uma família de aves columbiformes que inclui os pombos, pombas, picaús, rolas e rolinhas.', 'Columbídeos', '12');
INSERT INTO `zooinfo`.`familia` (`codigo`, `descricaoFamilia`, `nomeFamilia`, `claCodigo`) VALUES ('21', 'Rheidae é uma família de aves ratitas que não voam e apareceu pela primeira vez no Paleoceno. Hoje é representado pelo único gênero vivo Rhea, mas também contém vários gêneros extintos.', 'Rheidae', '12');
INSERT INTO `zooinfo`.`familia` (`codigo`, `descricaoFamilia`, `nomeFamilia`, `claCodigo`) VALUES ('22', 'Phasianidae é uma família de aves galliformes de corpo robusto, pernas e asas curtas. O grupo é bastante variado e inclui, por exemplo, a galinha, o pavão, o faisão, perdizes, tetrazes e o peru.', 'Phasianidae', '12');
INSERT INTO `zooinfo`.`familia` (`codigo`, `descricaoFamilia`, `nomeFamilia`, `claCodigo`) VALUES ('23', 'Cathartidae é uma família de aves distribuída no continente americano. Popularmente, podem ser chamados de \"condores\", \"urubus\" ou \"abutres do Novo Mundo\", entretanto não são aparentados com os abutres verdadeiros.', 'Cathartidae', '12');
INSERT INTO `zooinfo`.`familia` (`codigo`, `descricaoFamilia`, `nomeFamilia`, `claCodigo`) VALUES ('24', 'O beija-flor, também conhecido como colibri, cuitelo, chupa-flor, pica-flor, chupa-mel, binga, guanambi, guinumbi, guainumbi, guanumbi e mainoĩ, é uma ave da família Trochilidae, composta por 108 gêneros e 322 espécies conhecidas.', 'Trochelidae', '12');
INSERT INTO `zooinfo`.`familia` (`codigo`, `descricaoFamilia`, `nomeFamilia`, `claCodigo`) VALUES ('25', 'Os equinos constituem uma família de mamíferos perissodáctilos. Esta família abarca apenas o género Equus, onde se classificam o cavalo e o burro, além da zebra.', 'Equidae', '13');
INSERT INTO `zooinfo`.`familia` (`codigo`, `descricaoFamilia`, `nomeFamilia`, `claCodigo`) VALUES ('26', 'Suidae é uma família de mamíferos artiodáctilos. Esta família taxonómica inclui vários géneros, nos quais se encontram espécies de animais domésticos, como o porco-doméstico, e selvagens tais como o javali, o facocero e o babirussa.', 'Suidae', '13');
INSERT INTO `zooinfo`.`familia` (`codigo`, `descricaoFamilia`, `nomeFamilia`, `claCodigo`) VALUES ('27', 'Os bovídeos constituem uma família de mamíferos ruminantes, à qual pertencem animais domésticos, como ovelha, cabra e bois, além de selvagens, como os antílopes e bisontes.', 'Bovidae', '13');
INSERT INTO `zooinfo`.`familia` (`codigo`, `descricaoFamilia`, `nomeFamilia`, `claCodigo`) VALUES ('28', 'Caviidae ou cavídeos é uma família de roedores sul-americanos onde estão incluídos a capivara, o mocó, a mara, o preá e o porquinho-da-Índia, entre outros.', 'Cavidae', '13');
INSERT INTO `zooinfo`.`familia` (`codigo`, `descricaoFamilia`, `nomeFamilia`, `claCodigo`) VALUES ('29', 'A família Leporidae pertence à ordem dos mamíferos lagomorfos e inclui cerca de 60 espécies de coelhos e lebres. Os leporídeos distinguem-se das pikas pelas orelhas e patas traseiras compridas.', 'Leporidae', '13');
INSERT INTO `zooinfo`.`familia` (`codigo`, `descricaoFamilia`, `nomeFamilia`, `claCodigo`) VALUES ('30', 'Felinos são uma família de animais mamíferos digitígrados, da ordem dos carnívoros. Existem muitas espécies selvagens, como os grandes felinos. Existem duas subfamílias de felídeos: Pantherinae e Felinae.', 'Felidae', '13');
INSERT INTO `zooinfo`.`familia` (`codigo`, `descricaoFamilia`, `nomeFamilia`, `claCodigo`) VALUES ('31', 'Canidae é uma família de mamíferos da ordem Carnivora que engloba cães, lobos, chacais, coiotes e raposas. É composto por 35 espécies distribuídas por todos os continentes com exceção da Antártica.', 'Canidae', '13');
INSERT INTO `zooinfo`.`familia` (`codigo`, `descricaoFamilia`, `nomeFamilia`, `claCodigo`) VALUES ('32', 'Os Ursos constituem uma família de mamíferos plantígrados, da ordem Carnivora, geralmente de grande porte, contendo os ursos e os pandas.', 'Ursidae', '13');
INSERT INTO `zooinfo`.`familia` (`codigo`, `descricaoFamilia`, `nomeFamilia`, `claCodigo`) VALUES ('33', 'Os Hominídeos formam uma família taxonômica dos grandes primatas', 'Hominidae', '13');
INSERT INTO `zooinfo`.`familia` (`codigo`, `descricaoFamilia`, `nomeFamilia`, `claCodigo`) VALUES ('34', 'Giraffidae é uma família de artiodáctilos que surgiu no final do Oligoceno. Atualmente contém apenas dois gêneros: Okapia e Giraffa.', 'Giraffidae', '13');
INSERT INTO `zooinfo`.`familia` (`codigo`, `descricaoFamilia`, `nomeFamilia`, `claCodigo`) VALUES ('35', 'Os crocodilianos ou crocodilos são os répteis da ordem Crocodilia, na qual existem 24 espécies vivas e numerosos fósseis. São répteis maioritariamente grandes, predadores e semiaquáticos.', 'Crocodylia', '11');
INSERT INTO `zooinfo`.`especie` (`codigo`, `descricaoEspecie`, `nomeEspecie`, `famCodigo`) VALUES ('1', 'O tigre-de-bengala (nome científico: Panthera tigris tigris), também conhecido como tigre-indiano, é um grande felino e uma das 6 subespécies de tigre restantes, sendo atualmente a maior delas.', 'Tigre-de-bengala', '30');
INSERT INTO `zooinfo`.`especie` (`codigo`, `descricaoEspecie`, `nomeEspecie`, `famCodigo`) VALUES ('2', 'O termo chimpanzé aplica-se aos primatas do gênero Pan, da família Hominidae, subfamília Homininae', 'Chimpanzé', '33');
INSERT INTO `zooinfo`.`especie` (`codigo`, `descricaoEspecie`, `nomeEspecie`, `famCodigo`) VALUES ('3', 'Animal terrestre vivo mais alto e o maior ruminante . É tradicionalmente considerada uma espécie , Giraffa camelopardalis , com nove subespécies. ', 'Girafa', '34');
INSERT INTO `zooinfo`.`especie` (`codigo`, `descricaoEspecie`, `nomeEspecie`, `famCodigo`) VALUES ('4', 'Mamífero carnívoro do gênero Panthera e da família Felidae. A espécie é atualmente encontrada na África subsaariana e na Ásia, com uma única população remanescente em perigo.', 'Leão', '30');
INSERT INTO `zooinfo`.`especie` (`codigo`, `descricaoEspecie`, `nomeEspecie`, `famCodigo`) VALUES ('5', 'Os dedos estão ligados por membranas interdigitais. As narinas na ponta do focinho permitem-lhe respirar enquanto o corpo está submerso. Distingue-se  do crocodilo por ter o  4º dente na mandíbula.', 'Aligátor-americano', '35');
INSERT INTO `zooinfo`.`especie` (`codigo`, `descricaoEspecie`, `nomeEspecie`, `famCodigo`) VALUES ('6', 'Esta é a maior espécie de arara. A coloração forte em azul-cobalto, contrastante com a pele nua amarela em torno dos olhos e da mandíbula inferior distingue-a de todas as outras. O bico é preto e particularmente encurvado e pontiagudo.', 'Arara-jacinta', '12');
INSERT INTO `zooinfo`.`especie` (`codigo`, `descricaoEspecie`, `nomeEspecie`, `famCodigo`) VALUES ('7', 'Apresenta pele nua, nas faces, de cor branca e com pequenas penas escuras, que formam riscas paralelas. Distingue-se pela plumagem verde na coroa e na fronte. Sendo considerada uma das mais bonitas espécies de arara.', 'Arara-azul-e-amarela','12');
INSERT INTO `zooinfo`.`especie` (`codigo`, `descricaoEspecie`, `nomeEspecie`, `famCodigo`) VALUES ('8', 'Distingue-se pela coloração viva, onde sobressaem a fronte e a coroa vermelhas. Em torno dos olhos e na base do bico tem pele nua rosada. Não existe dimorfismo sexual evidente, machos e fêmeas não se distinguem por observação.', 'Arara-de-fronte-vermelha', '12');
INSERT INTO `zooinfo`.`especie` (`codigo`, `descricaoEspecie`, `nomeEspecie`, `famCodigo`) VALUES ('9', 'Apresenta tipicamente cauda curta, tufos de pêlos negros em forma de pincel na extremidade das orelhas e membros longos. O lince-ibérico, apresenta um padrão de pintas negras muito denso, e os tufos de pêlo em cada lado da face.', 'Lince-ibérico', '30');
INSERT INTO `zooinfo`.`especie` (`codigo`, `descricaoEspecie`, `nomeEspecie`, `famCodigo`) VALUES ('10', 'É um antílope de tamanho médio. Distingue-se pela risca longitudinal de pelo negro no focinho, junto aos olhos. Só o macho tem cornos, que são voltados para trás, em forma de lira e com espessos anéis concêntricos.', 'Impala-de-face-negra', '27'); 
